mapper.insert:
		

		insert into food_manage(
		food_id
		,food_name
		,food_size
		,food_unit
		,food_shelflife
		,staff_id
		,food_date)
		select
		concat('FN',(select lpad(count(*)+1,4,'0') from food_manage))
		,#{food_name}
		,#{food_size}
		,#{food_unit}
		,#{food_shelflife}
		,'staff'
		,#{food_date}
