mapper.insert:
	insert into menu(
 menu_id
 ,menu_name
 ,menu_cate
 ,menu_price
 ,menu_sprice
 ,menu_sale
 ,menu_kcal
 ,menu_picture
 ,menu_explanation)		
select
concat('MN',(select date_format(sysdate(),'%y%m%d%s')))
,#{menu_name}	
,#{menu_cate}
,#{menu_price}
,#{menu_sprice}
,#{menu_sale}
,#{menu_kcal}
,#{menu_picture}
,#{menu_explanation}

mapper.update:
update menu
set menu_name = #{menu_name}
,menu_cate= #{menu_cate}
,menu_price= #{menu_price}
,menu_sprice= #{menu_sprice}
,menu_kcal= #{menu_kcal}
where menu_id= #{menu_id}
