mapper.insert:
insert into ep_order_manage( 
ep_id
,food_id
,ep_name
,ep_phone
,ep_director
,ep_address
,ep_text)
select
concat('EPMN',(select date_format(sysdate(),'%y%m%d%s')))
,'food_id'
,#{ep_name}
,#{ep_phone}
,#{ep_director}
,#{ep_address}
,#{ep_text}

mapper.update:
update ep_order_manage
set food_id = #{food_id}
,ep_phone= #{ep_phone}
,ep_name= #{ep_name}
,ep_director= #{ep_director}
,ep_address= #{ep_address}
,ep_text= #{ep_text}
where ep_id= #{ep_id}
